const axios = require('axios');

/**
 * Fetch JWKS (JSON Web Key Set) from the configured endpoint.
 * @returns {Promise<Object>} The JWKS object.
 */
async function fetch_jwks() {
  // Use underscore for field names as per user instruction
  const jwks_endpoint = 'https://api.stack-auth.com/api/v1/projects/766ab62c-c23c-42f8-a86d-c54110398d2b/.well-known/jwks.json';
  try {
    const response = await axios.get(jwks_endpoint);
    return response.data;
  } catch (error) {
    throw new Error('Failed to fetch JWKS: ' + error.message);
  }
}

module.exports = { fetch_jwks };
