const fs = require('fs');
const path = require('path');

function cleanLockfile() {
  console.log('ðŸ§¹ Cleaning lockfile to avoid config mismatch...');
  
  const lockfile_path = 'pnpm-lock.yaml';
  
  if (fs.existsSync(lockfile_path)) {
    console.log('  Removing pnpm-lock.yaml...');
    fs.unlinkSync(lockfile_path);
  }
  
  // Also remove any package-lock.json or yarn.lock
  const other_lockfiles = ['package-lock.json', 'yarn.lock'];
  
  other_lockfiles.forEach(file => {
    if (fs.existsSync(file)) {
      console.log(`  Removing ${file}...`);
      fs.unlinkSync(file);
    }
  });
  
  console.log('âœ… Lockfile cleanup completed');
}

cleanLockfile();
